import React, { createContext, useContext, useEffect, useState } from "react";
import api from "../api";

export const AuthContext = createContext({});

export const AuthProvider = (props) => {
  const [auth, setAuth] = useState(false);
  const [user, setUser] = useState();
  const [article, setArticle] = useState([]);

  const checkToken = async (token) => {
    const response = await api
      .get("/auth/check", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((res) => {
        setAuth(true);
        return res;
      })
      .catch((err) => {
        return false;
      });
    return response;
  };

  const handleLogin = async (username, password) => {
    const response = await api
      .post("/auth/signin", {
        username,
        password,
      })
      .then((res) => {
        const token = localStorage.setItem("token", res.data.access.token);
        setAuth(true);
        return res;
      })
      .catch((err) => {
        return false;
      });
    return response;
  };

  const handleResgister = async (email, username, password) => {
    const response = await api
      .post("/auth/signup", {
        email,
        username,
        password,
      })
      .then((res) => {
        return res;
      })
      .catch((err) => {
        return err;
      });
    return response;
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setAuth(false);
  };

  const handleCreate = async (title, description, body) => {
    const token = localStorage.getItem("token");
    const response = await fetch(
      "https://dtsistema.herokuapp.com/client/create",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        title,
        description,
        body,
      }
    )
      .then((res) => {
        return res;
      })
      .catch((err) => {
        return err;
      });
    // const response = await api
    //   .post("/article/create", {
    //     headers: {
    //       Authorization: `Bearer ${token}`,
    //     },
    //     title,
    //     description,
    //     body,
    //     // body: JSON.stringify(body),
    //   })
    //   .then((res) => {
    //     console.log(res);
    //     return res;
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //     return err;
    //   });
    // return response;
  };

  const handleUpdate = async () => {
    /// Atualizar
  };

  const handleDelete = async () => {
    /// Deletar
  };

  const handleArticlesResult = async () => {
    const token = localStorage.getItem("token");
    const response = await api
      .get("/article/all", {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      })
      .then((res) => {
        return res;
      })
      .catch((err) => {
        return err;
      });
    return response;
  };

  useEffect(() => {
    (async () => {
      const getResults = await handleArticlesResult();
      setArticle(getResults.data);
    })();
  }, []);

  return (
    <AuthContext.Provider
      value={{
        auth,
        setAuth,
        user,
        setUser,
        handleLogin,
        handleResgister,
        checkToken,
        handleLogout,
        handleCreate,
        handleDelete,
        handleUpdate,
        handleArticlesResult,
        article,
        setArticle,
      }}
    >
      {props.children}
    </AuthContext.Provider>
  );
};

export const UseDataContext = () => useContext(AuthContext);
