import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  min-height: 100vh;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

export const Body = styled.div`
  display: flex;
  width: 70%;
  flex-direction: column;
`;

export const Input = styled.input`
  display: flex;
  padding: 10px;
  margin-bottom: 10px;
`;
