import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api";
import {
  Body,
  Container,
  Input,
  ContainerInput,
  ContainerButton,
  Button,
  TextLogin,
  Text,
} from "../styled/SignIn";

export default function SignInPage() {
  const navigate = useNavigate();
  const [username, setUserName] = useState();
  const [password, setPassword] = useState();

  async function handleSignin() {
    const login = await api.handleLogin(username, password);
    if (login.data) {
      localStorage.setItem("token", login.data.access.token);
      window.location.replace("/");
    }
  }

  return (
    <Container>
      <Body>
        <ContainerInput>
          <Input
            placeholder="Usuario"
            type="text"
            value={username}
            onChange={(e) => setUserName(e.target.value)}
          />
          <Input
            placeholder="Senha"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </ContainerInput>
        <ContainerButton>
          <TextLogin>Email ou senha incorreta</TextLogin>
          <TextLogin>
            Nao possui conta ? <Text to="/signup">Cadastrar</Text>
          </TextLogin>
          <Button onClick={handleSignin}>Entrar</Button>
        </ContainerButton>
      </Body>
    </Container>
  );
}
