import React, { useState } from "react";
import Modal from "react-modal";

function ModalPage() {
  const [modalOpen, setModalOpen] = useState(false);

  function openModal() {
    setModalOpen(true);
  }

  function closeModal() {
    setModalOpen(false);
  }

  const customStyles = {
    content: {
      display: "flex",
      flexDirection: "column",
      textAlign: "center",
      top: "100px",
      left: "100px",
      right: "100px",
      bottom: "100px",
      backgroundColor: "#ccc",
    },
  };

  return (
    <>
      <button onClick={openModal}>Adicionar</button>
      <Modal
        isOpen={modalOpen}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <div>
          <h2>Modal</h2>
          <button
            onClick={closeModal}
            style={{ position: "absolute", top: "20px", right: "20px" }}
          >
            X
          </button>
        </div>

        <form style={{ display: "flex", border: "solid 1px", flex: "1" }}>
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              width: "100%",
            }}
          >
            <input
              type="text"
              placeholder="Title"
              style={{ marginBottom: "10px", padding: "10px" }}
            />
            <input
              type="text"
              placeholder="Description"
              style={{ marginBottom: "10px", padding: "10px" }}
            />
            <input
              type="text"
              placeholder="Body"
              style={{ marginBottom: "10px", padding: "10px" }}
            />
            <button>Criar</button>
          </div>
        </form>
      </Modal>
    </>
  );
}

export default ModalPage;
