    //     if (res.status === 200) {
    //       setAuth(true);
    //     }
    //   });
  }

  useEffect(() => {
    authPermissions();
  }, []);

  return (
    <Routes>
      <Route path="/" end element={auth ? <HomePage /> : <SignInPage />} />
      <Route path="/signin" element={<SignInPage />} />
      <Route path="/signup" element={<SignUpPage />} />
    </Routes>
  );
}
