import React, { createContext, useContext, useEffect, useState } from "react";
import api from "../api";

export const AuthContext = createContext({});

export const AuthProvider = (props) => {
  const [auth, setAuth] = useState(false);
  const [user, setUser] = useState();

  const checkToken = async (token) => {
    const response = await api
      .get("/auth/check", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((res) => {
        setAuth(true);
        return res;
      })
      .catch((err) => {
        return false;
      });
    return response;
  };

  const handleLogin = async (username, password) => {
    const response = await api
      .post("/auth/signin", {
        username,
        password,
      })
      .then((res) => {
        const token = localStorage.setItem("token", res.data.access.token);
        setAuth(true);
        return res;
      })
      .catch((err) => {
        return false;
      });
    return response;
  };

  const handleResgister = async (email, username, password) => {
    const response = await api
      .post("/auth/signup", {
        email,
        username,
        password,
      })
      .then((res) => {
        return res;
      })
      .catch((err) => {
        return err;
      });
    return response;
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setAuth(false);
  };

  const handleCreate = async () => {
    const response = await api.post("/article/create", {
      username,
      password,
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  };

  const handleUpdate = async () => {
    /// Atualizar
  };

  const handleDelete = async () => {
    /// Deletar
  };

  return (
    <AuthContext.Provider
      value={{
        auth,
        setAuth,
        user,
        setUser,
        handleLogin,
        handleResgister,
        checkToken,
        handleLogout,
      }}
    >
      {props.children}
    </AuthContext.Provider>
  );
};

export const UseDataContext = () => useContext(AuthContext);
