import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Body,
  Container,
  Input,
  ContainerInput,
  ContainerButton,
  Button,
  TextLogin,
  Text,
} from "../styled/SignUp";
import api from "./../api";

export default function SignUpPage() {
  const navigate = useNavigate();
  const [email, setEmail] = useState();
  const [username, setUserName] = useState();
  const [password, setPassword] = useState();

  async function handleResgister() {
    await api
      .post("/auth/signup", {
        email,
        username,
        password,
      })
      .then((res) => {
        if (res.data) {
          navigate("/signin");
        }
      })
      .catch((err) => console.log(err));
  }

  return (
    <Container>
      <Body>
        <ContainerInput>
          <Input
            placeholder="Email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <Input
            placeholder="Usuario"
            type="text"
            value={username}
            onChange={(e) => setUserName(e.target.value)}
          />
          <Input
            placeholder="Senha"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </ContainerInput>
        <ContainerButton>
          <TextLogin>
            Ja possui conta ? <Text to="/signin">Entrar</Text>
          </TextLogin>
          <Button onClick={handleResgister}>Cadastrar</Button>
        </ContainerButton>
      </Body>
    </Container>
  );
}
