import React, { useState } from "react";
import Modal from "react-modal";

function ModalPage() {
  const [modalOpen, setModalOpen] = useState(false);

  function openModal() {
    setModalOpen(true);
  }

  function closeModal() {
    setModalOpen(false);
  }

  const customStyles = {
    content: {
      position: "absolute",
      textAlign: "center",
      top: "100px",
      left: "100px",
      right: "100px",
      bottom: "100px",
      backgroundColor: "#ccc",
    },
  };

  return (
    <>
      <button onClick={openModal}>Adicionar</button>
      <Modal
        isOpen={modalOpen}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <div>
          <h2>Ola</h2>
          <button onClick={closeModal}>X</button>
        </div>

        <div>Modal</div>
        <form>
          <input type="text" />
        </form>
      </Modal>
    </>
  );
}

export default ModalPage;
